bool _ConfigPanelIsOpen = false;







void Config__ResponseJSON(String json, bool needRestart = false) {
	json.replace("'", "\"");
	_Server.send(200, "text/json", json);
	if (needRestart) {
		_ConfigPanelIsOpen = false;
		delay(500);
		Tools__Reboot_Task.enableIfNot();
		delay(500);
		Tools__Reboot();
	}
}


void Config__APIFirmware() {
	HTTPUpload& upload = _Server.upload();
	if (upload.status == UPLOAD_FILE_START) {
		if (SOFTWARE_DEBUGMODE) Debug_ConsolePrint("Update: ");
		if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln(upload.filename.c_str());
		if (!Update.begin(UPDATE_SIZE_UNKNOWN)) {  //start with max available size
			                                       // Update.printError(Serial);
		}
	} else if (upload.status == UPLOAD_FILE_WRITE) {
		// flashing firmware to ESP
		if (Update.write(upload.buf, upload.currentSize) != upload.currentSize) {
			// Update.printError(Serial);
		}
	} else if (upload.status == UPLOAD_FILE_END) {
		if (Update.end(true)) {  //true to set the size to the current progress
			if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln("Update Success");
		} else {
			if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln("Update Error");
			//Update.printError(Serial);
		}
	}
}


