void Config__APISet() {
	_Server.sendHeader("Connection", "close");
	if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln("\r\n***** API-Set Recieved Data ******");
	//.......................................................................
	_ConfigTimeout = millis() + (10 * 60000);  //Reset timeout for next 10 min
	//.......................................................................
	String res = "";
	try {
		if (!_ConfigPanelIsOpen) return;
		String data = "";
		//Serial.print("\r\n\r\n...................\r\ndata: ");
		if (_Server.hasArg("data")) data = String(_Server.arg("data"));
		else if (_Server.hasArg("Data")) data = String(_Server.arg("Data"));
		else if (_Server.hasArg("DATA")) data = String(_Server.arg("DATA"));
		//Serial.println(data);
		if (data == "") {
			String noNeed = ",RANDOM,Random,random,Rnd,RND,rnd,TOKEN,Token,token,TKN,Tkn,tkn";
			for (int i = 0; i < _Server.args(); i++) {
				if (noNeed.indexOf(_Server.argName(i)) >= 0) continue;
				data = String(_Server.arg(i));
				break;
			}
			//Serial.println("data imported");
			//Serial.println(data);
		}
		//Serial.println("\r\n\r\n..................");
		//...................................................................
		if (data != "") {
			int max = 1024 + (2 * data.length());
			DynamicJsonDocument doc(max);
			doc.clear();
			deserializeJson(doc, data.c_str());
			JsonObject jsn;
			bool hasRoot = (data.substring(0, 10).indexOf("\"root\"") > 0 ? true : false);
			//............................................................................

			String mode = "";
			String sec = "";
			if (hasRoot) jsn = doc["root"].as<JsonObject>();
			else jsn = doc.as<JsonObject>();
			try {
				if (data.indexOf("\"requestMode\"") > 0) mode = jsn["requestMode"].as<String>();
				else if (data.indexOf("\"mode\"") > 0) mode = jsn["mode"].as<String>();
				mode.toLowerCase();
			} catch (...) {}
			try {
				if (data.indexOf("\"section\"") > 0) sec = jsn["section"].as<String>();
				sec.toLowerCase();
			} catch (...) {}
			if (mode == "network") {
				sec = "network";
				mode = "";
			}
			if (mode == "reboot" || mode == "close") {
				sec = "general";
				mode = "exit";
			}
			//...................................................... SETTING
			if (sec == "setting") {
				if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln("API-Set: Setting");
				if (mode == "command") {
					String dvc = "";
					String key = "";
					String val = "";
					try {
						if (data.indexOf("\"dvc\"") > 0) dvc = jsn["dvc"].as<String>();
						dvc.toLowerCase();
					} catch (...) {}
					try {
						if (data.indexOf("\"key\"") > 0) key = jsn["key"].as<String>();
						key.toLowerCase();
					} catch (...) {}
					try {
						if (data.indexOf("\"val\"") > 0) val = jsn["val"].as<String>();
						val.toLowerCase();
					} catch (...) {}
					if (dvc == "xxxxx") {
						res = "{'result':'wrong'}";
					} else {
						res = "{'result':'wrong'}";
					}
				}
				//............................ Set Address & Scenario
				else {
					if (mode == "all" || mode == "address") res += ",'address':'" + Config__JSONSet_Address(doc) + "'";
					if (mode == "all" || mode == "scenario") res += ",'scenario':'" + Config__JSONSet_Scenario(doc) + "'";
					if (mode == "all" || mode == "runsen") res += ",'runsen':'" + Config__JSONSet_Runsen(doc) + "'";
					if (res != "") res = "{'result':'ok'," + res.substring(1) + "}";
					else res = "{'result':'wrong'}";
				}
				res.replace("'", "\"");
				_Server.send(200, "text/json", res);
				return;
			}
			//...................................................... UPDATE
			else if (sec == "update") {
				if (mode == "clean") {
					MemoClean(EP_MEMORYSTART, EEPROM_SIZE);
					res = "{'result':'ok'}";
					res.replace("'", "\"");
					_Server.send(200, "text/json", res);
					return;
				}
			}
			//...................................................... GENERAL
			else if (sec == "general") {
				if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln("API-Get: general");
				//..................................................
				if (mode == "reboot") {
					if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln("API-Config: Reboot All");
					Tools__SetMode("reboot_sendall", true);
					Config__ResponseJSON("{'result':'ok'}", true);
					return;
				} else if (mode == "exit") {
					if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln("API-Config: Exit (Closing Config)");
					Tools__Memory_StrSet("_StartMode", "normal");
					Config__ResponseJSON("{'result':'ok'}", true);
					return;
				}
			}
			//...................................................... NETWORK
			else if (sec == "network") {
				if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln("API-Get: Network");
				bool sendtoall = false;
				try {
					if (data.indexOf("\"target\"") > 0) sendtoall = (jsn["target"].as<String>() == "all" ? true : false);
				} catch (...) {}
				if ((mode == "save" || mode == "restore") && sendtoall) {
					Tools__Memory_StrSet("_MeshNameOld", _MeshName);
					Tools__Memory_StrSet("_MeshPassOld", _MeshPass);
					Tools__Memory_StrSet("_MeshChannelOld", String(_MeshChannel));
				}
				//..................................................
				if (mode == "restore") {
					if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln("API-Config: Network-Restore");
					Tools__SettingDefault();
					Tools__SettingSave();
					Tools__Memory_StrSet("_StartMode", sendtoall ? "config_sendall" : "normal");
					Config__ResponseJSON("{'result':'ok'}", true);
					return;
				}
				//..................................................
				else {
					if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln("API-Config: Network-Save");
					try {
						if (data.indexOf("\"extender\"") > 0) _Extender = (jsn["extender"].as<String>() == "1" ? 1 : 0);
					} catch (...) {}
					try {
						if (data.indexOf("\"circuit\"") > 0) _Circuit = jsn["circuit"].as<String>();
					} catch (...) {}
					try {
						if (data.indexOf("\"projectId\"") > 0) _ProjectID = jsn["projectId"].as<String>();
					} catch (...) {}
					try {
						if (data.indexOf("\"encryptionKey\"") > 0) _EncryptionKey = jsn["encryptionKey"].as<String>();
					} catch (...) {}
					try {
						if (data.indexOf("\"modemSsid\"") > 0) _ModemSSID = jsn["modemSsid"].as<String>();
					} catch (...) {}
					try {
						if (data.indexOf("\"modemPass\"") > 0) _ModemPass = jsn["modemPass"].as<String>();
					} catch (...) {}
					String ch = "";
					try {
						if (data.indexOf("\"modemChannel\"") > 0) ch = jsn["modemChannel"].as<String>();
					} catch (...) {}
					_ModemChannel = Tools__StringToByte(ch);
					try {
						if (data.indexOf("\"meshChannel\"") > 0) ch = jsn["meshChannel"].as<String>();
					} catch (...) {}
					_MeshChannel = Tools__StringToByte(ch);
					try {
						if (data.indexOf("\"meshName\"") > 0) _MeshName = jsn["meshName"].as<String>();
					} catch (...) {}
					try {
						if (data.indexOf("\"meshPass\"") > 0) _MeshPass = jsn["meshPass"].as<String>();
					} catch (...) {}
					try {
						if (data.indexOf("\"mqttBroker\"") > 0) _MqttBroker = jsn["mqttBroker"].as<String>();
					} catch (...) {}
					try {
						if (data.indexOf("\"mqttUser\"") > 0) _MqttUser = jsn["mqttUser"].as<String>();
					} catch (...) {}
					try {
						if (data.indexOf("\"mqttPass\"") > 0) _MqttPass = jsn["mqttPass"].as<String>();
					} catch (...) {}
					Tools__SettingSave();
					_Configured = 1;
					Tools__Memory_StrSet("_Configured", "1");
					if (mode == "save" && sendtoall) {
						Tools__Memory_StrSet("_StartMode", sendtoall ? "config_sendall" : "normal");
						Config__ResponseJSON("{'result':'ok'}", true);
						return;
					} else {
						Config__ResponseJSON("{'result':'ok'}");
						return;
					}
				}
			}
			//...................................................... TUNING
			else if (sec == "tuning") {
				if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln("API-Get: Tuning");
				//............................................
				/*
				if (mode == "touch") {
					if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln("API-Config: Tuning-Touch");
					
					Config__ResponseJSON("{'result':'ok'}");
					return;
				}
				*/
				Config__ResponseJSON("{'result':'wrong'}");
			} else {
				Config__ResponseJSON("{'result':'wrong'}");
			}
			doc.clear();
		}
	} catch (const std::exception& ex) {
		if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln("API-Config Error: " + String(ex.what()));
	}
	if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln("*********************");
}






String Config__JSONGet_Scenario() {
	String tmp, d = "";
	try {
		byte j, num, s, id;
		for (s = 0; s < 10; s++) {
			id = (s <= 6 ? s + 1 : s + 94);
			d += (d.length() > 0 ? "," : "");
			d += "'" + String(id) + "':{'id':'" + Tools__ByteToHexstring(id) + "'";
			d += ",'act':'" + Tools__ByteToHexstring(_Scenario_List[s].Act) + "'";
			d += ",'name':'" + CA_ToString(_Scenario_List[s].Name) + "'";
			d += ",'relay':{";
			for (j = 0; j < RELAY_MAX; j++) {
				d += (j == 0 ? "'" : ",'") + String(j + 1) + "':'" + Tools__ByteToHexstring(_Scenario_List[s].Relay[j]) + "'";
			}
			d += "},'virtual':{";
			for (j = 0; j < 3; j++) {
				num = _Scenario_List[s].Virtual[j];
				d += (j == 0 ? "'" : ",'") + String(j + 1) + "':{'cmd':'" + (num == 255 ? "FF" : "01") + "','val':'" + (num == 255 ? "" : String(num)) + "'}";
			}
			d += "},'code':{";
			for (j = 0; j < 3; j++) {
				num = _Scenario_List[s].CodeCmd[j];
				if (num != 1 && num != 2) num = 255;
				tmp = CA_ToString(_Scenario_List[s].CodeVal[j]);
				//tmp = Tools__StringToHexStr(tmp);
				d += (j == 0 ? "'" : ",'") + String(j + 1) + "':{'cmd':'" + Tools__ByteToHexstring(num) + "','val':'" + tmp + "'}";
			}
			d += "}";


			d += "}";
		}
		d = "'scenario':{" + d + "}";
	} catch (const std::exception& ex) {}
	return d;
}


