void Config__Loop() {
	while (true) {
		try {
			_Server.handleClient();
			//........................ Panel Timeout
			int timer = (_ConfigTimeout - millis());
			if (timer < 0) {
				Tools__Reboot();
			}
		} catch (const std::exception& ex) {
			if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln("Config__Loop Error: " + String(ex.what()));
		}
		delay(TASK_LOOP_DELAY);
	}
}


void Config__Setup(void* param) {
	if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln("\r\nStart Config_Mode ... \r\n");

	//...................................... Set Powercut Restart-Point
	Tools__Memory_StrSet("_StartMode", "normal");

	//....................................
	_ConfigTimeout = millis() + (10 * 60000);  //Set timeout on next 5 min

	IPAddress local_IP(192, 168, 1, 1);
	IPAddress subnet(255, 255, 255, 0);

	//....................................
	WiFi.softAPConfig(local_IP, local_IP, subnet);
	delay(2500);

	String wifiName = "Zooril_" + DVC_TYPE + "_" + _SerialNo;
	String wifiPass = DVC_DEFAULTPASS;
	char wN[wifiName.length()];
	wifiName.toCharArray(wN, wifiName.length() + 1);
	char wP[wifiPass.length()];
	wifiPass.toCharArray(wP, wifiPass.length() + 1);
	WiFi.softAP(wN, wP);
	if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln("WifiName : " + String(wN) + "  &   Pass : " + String(wP));

	delay(1000);
	WiFi.softAPConfig(local_IP, local_IP, subnet);

	if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln(WiFi.softAPIP().toString());

	//....................................
	// const char* host = "esp32";
	while (!MDNS.begin("esp32")) {
		if (SOFTWARE_DEBUGMODE) Debug_ConsolePrintln("Error setting up MDNS responder!");
		delay(1000);
	}


	/*
    _Server.on("/jquery321min.js", HTTP_GET, [](WebServerRequest *request){
    request->send(SPIFFS, "/jquery321min.js", "text/javascript");
    });
  */
	_Server.on("/jquery.js", Config__FileJquery);
	_Server.on("/main.js", Config__FileMainScript);
	_Server.on("/setting.js", Config__FileSettingScript);
	_Server.on("/style.css", Config__FileMainCSS);

	_Server.on("/alive", Config__APIAlive);
	_Server.on("/set", Config__APISet);
	_Server.on("/get", Config__APIGet);
	_Server.on("/config", Config__PageHtml);
	_Server.on("/", Config__PageHtml);
	_Server.on("/firmware", HTTP_POST, Config__PageFirmware, Config__APIFirmware);


	_Server.begin();
	_ConfigPanelIsOpen = true;
	//Dvc__SetPinout();
	//........................................... Stay In Loop
	Config__Loop();
}
